import React from "react";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

function EditAvatarPopup(props) {

    const currentUser = React.useContext(CurrentUserContext);

    const [avatar, setAvatar] = React.useState('');

    function handleAvatarChange(e) {
        setAvatar(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault();
        props.onUpdateAvatar(avatar);
    }

    return(
        <PopupWithForm name="avatar" title="Обновить аватар" buttonText="Сохранить" isOpen={props.isOpen} onClose={props.onClose} children={
            <>
                <label className="popup__field">
                    <input value={avatar} onChange={handleAvatarChange} type="url" name="link" required className="popup__input popup__input_type_link"
                           placeholder="Ссылка на картинку" id="avatar-link"/>
                    <span className="popup__input-error avatar-link-error"></span>
                </label>
            </>
        }/>
    )
}

export default EditAvatarPopup
