import React from "react";
import api from '../utils/Api.js'

function Main(props) {

    const [userName, setUserName] = React.useState();
    const [userDescription, setUserDescription] = React.useState();
    const [userAvatar, setUserAvatar] = React.useState();

    const [cards, setCards] = React.useState([])

    React.useEffect(() => {
        api.getUserInfo()
            .then(res => {
                setUserName(res.name);
                setUserDescription(res.about);
                setUserAvatar(res.avatar);
            })
            .catch(err => console.log(err))
    })

    return(
        <main className="content">
            <section className="profile">
                <div className="profile__data">
                    <div onClick={props.onEditAvatar} className="profile__avatar-container">
                        <img src={userAvatar} alt="аватар" className="profile__avatar"/>
                        <div className="profile__avatar-hover"></div>
                    </div>
                    <div className="profile__info">
                        <div className="profile__name-edit">
                            <h1 className="profile__name">{userName}</h1>
                            <button onClick={props.onEditProfile} type="button" className="profile__edit-btn"></button>
                        </div>
                        <h2 className="profile__description">{userDescription}</h2>
                    </div>
                </div>
                <button onClick={props.onAddPlace} type="button" className="profile__add-btn"></button>
            </section>
            <section className="places">
                <template id="place">
                    <article className="place">
                        <img alt="место" className="place__image"/>
                        <div className="place__info">
                            <h2 className="place__name"></h2>
                            <div className="place__like-block">
                                <button type="button" className="place__like"></button>
                                <p className="place__like-count"></p>
                            </div>
                        </div>
                    </article>
                </template>
            </section>
        </main>
    )


}

export default Main
