const baseUrl = 'https://auth.nomoreparties.co';

const registration = (data) => {
    fetch(`${baseUrl}/signup`, {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify(data)
    })
        .then(res => {
            return res.ok
                ? res.json()
                : Promise.reject(`Ошибка ${res.status}`)
        })
}

export {registration}
